<?php/** * @Start notification helper class * @return */if (!class_exists('CS_Notification_Helper')) {    class CS_Notification_Helper {        public $message;		        public function __construct() {            // Do Something here..			$this->message=esc_html__('No record found','jobcareer');        }        // start function for success message        public function success($message = '') {            global $post;			if($message==''){				$message=$this->message;			}            $output = '';            $output .= '<div class="col-md-12 cs-succ_mess"><p>';            $output .= $message;            $output .= '</p></div>';            echo force_balance_tags($output);        }        // start function for error message        public function error($message = '') {            global $post;			if($message==''){				$message=$this->message;			}            $output = '';            $output .= '<div class="col-md-12 cs-error"><p>';            $output .= $message;            $output .= '</p></div>';            echo force_balance_tags($output);        }         // start function for warning message        public function warning($message = '') {            global $post;			if($message==''){				$message=$this->message;			}            $output = '';            $output .= '<div class="col-md-12 cs-warning"><p>';            $output .= $message;            $output .= '</p></div>';            echo force_balance_tags($output);        }        // start function for informations message        public function informations($message = '') {            global $post;			if($message==''){				$message=$this->message;			}            $output = '';            $output .= '<div class="col-md-12 cs-informations"><p>';            $output .= $message;            $output .= '</p></div>';            echo force_balance_tags($output);        }    }}$cs_notification = new CS_Notification_Helper();$cs_noti_vars = array('cs_notification');$cs_noti_vars = CS_JOBCAREER_GLOBALS()->globalizing($cs_noti_vars);extract($cs_noti_vars);